// Clear out any existing or erroneous entries
me.PurgeDataTableEntries();

// CreateInfoTableFromDataShape(infoTableName:STRING("InfoTable"), dataShapeName:STRING):INFOTABLE(PTCDTS.TWXLLM.ExampleData_DS)
let exampleData = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
    infoTableName: "InfoTable",
    dataShapeName: "PTCDTS.TWXLLM.ExampleData_DS"
});

// Generating random data using the method Math.random() * (max - min) + min, and rounding to 2 decimal places
let startDatetime = new Date("11/01/2024 00:00:00");
startDatetime = dateAddHours(startDatetime, 4); // Adjusting for timezone
// var result = startDatetime;
const assets = ['Machine1', 'Machine2', 'Machine3'];
let row;
let randomNum;
let runningThreshold = 0.8; //gives 80% chance that an asset will be listed as "running"
for (let i = 0; i < 2880; i++) { // 2880 hours between 12 AM November 1, 2024 and 12 AM March 1, 2025.
    for (let j = 0; j < assets.length; j++) {
    	row = {};
        row.id = (i * assets.length + j).toString();
        row.DateTime = startDatetime;
        row.asset = assets[j];
        randomNum = Math.random();
        if (randomNum < runningThreshold){
        	row.status = 'running';
        } else {
            row.status = 'idle';
        }
        row.pressure = parseFloat(Math.random() * (150 - 50) + 50).toFixed(2);
        row.temperature = parseFloat(Math.random() * (120 - 20) + 20).toFixed(2);
        row.speed = parseFloat(Math.random() * (1000 - 100) + 100).toFixed(2);
        row.vibration = parseFloat(Math.random() * (5 - 0) + 0).toFixed(2);
        row.voltage = parseFloat(Math.random() * (240 - 210) + 210).toFixed(2);
        row.flow = parseFloat(Math.random() * (100 - 10) + 10).toFixed(2);
        row.torque = parseFloat(Math.random() * (500 - 100) + 100).toFixed(2);
        row.rpm = parseFloat(Math.random() * (1500 - 500) + 500).toFixed(2);
        row.power = parseFloat(Math.random() * (500 - 50) + 50).toFixed(2);
        exampleData.AddRow(row);
    }
	startDatetime = dateAddHours(startDatetime, 1);
}
//var result = exampleData;

me.AddDataTableEntries({
	values: exampleData /* INFOTABLE */
});

let result;
let currentRowCount = me.GetDataTableEntryCount();
// 2880 hours from Nov 1, 2024 - Feb 28, 2025, 3 assets. 2880 * 3 = 8640
let pctComplete = parseFloat((currentRowCount / 8640) * 100).toFixed(2); 
if (pctComplete == 100){ 
    result = "Data is ready.";
} else if (pctComplete == 0){
    result = "Click 'Prepare Data' to begin.";
} else {
    result = "Preparing data: "+pctComplete+"% complete.";
}

try {
    let prompt = "Please write a query that can be used in ThingWorx to query data from a Data Table. The query should use the";
    prompt = prompt+ " {Data Table information} below to address the {user question}. Below is an {example query} to use for reference.";
    
    prompt = prompt+ ' {example query}:';
    prompt = prompt+ '"{';
    prompt = prompt+ '"filters":{';
    prompt = prompt+ '"type":"AND",';
    prompt = prompt+ '"filters":[';
    prompt = prompt+ '{';
    prompt = prompt+ '"type":"EQ",';
    prompt = prompt+ '"fieldName":"asset",';
    prompt = prompt+ '"value":"Machine2"';
    prompt = prompt+ '},{';
    prompt = prompt+ '"type":"Between",';
    prompt = prompt+ '"fieldName":"DateTime",';
    prompt = prompt+ '"from":"2024-12-01T00:00:00.000Z",';
    prompt = prompt+ '"to":"2024-12-31T23:59:59.999Z"';
    prompt = prompt+ '}]}}".';
    
    prompt = prompt+ " {user Question}: '"+userQuestion+"?'";
    
    prompt = prompt+ " {Data Table Information}: The names of the columns and their associated data types are: ";
    let fields = DataShapes["PTCDTS.TWXLLM.ExampleData_DS"].GetFieldDefinitions();
    let fieldIndex = 0;
    fields.rows.toArray().forEach(row => {
        prompt = prompt+ " Field "+fieldIndex+": '"+row.name+"' ("+row.baseType+"),";
        fieldIndex++;
    });
    prompt = prompt.slice(0, -1); 
    prompt = prompt+ ".";
    
    prompt = prompt+ " The assets in the Data Table are 'Machine1', 'Machine2', and 'Machine3'.";
    prompt = prompt+ " The data in the Data Table run from 11/01/2024 00:00:00 to 03/01/2025 00:00:00.";
    prompt = prompt+ " Data in the Data Table is recorded at an hourly rate.";
    prompt = prompt+ " The status for an asset at any given time is 'idle' or 'running'.";
     
    prompt = prompt+ " Next write JSON parameters for ThingWorx to use when aggregating the queried data into the answer requested";
    prompt = prompt+ " by the user. Use the {example parameters} for reference.";
    prompt = prompt+ " {example parameters}:";
    prompt = prompt+ '"{';
    prompt = prompt+ '"t":"returnedData",';
    prompt = prompt+ '"columns":"temperature",';
    prompt = prompt+ '"aggregates":"AVERAGE",';
    prompt = prompt+ '}".';
    
    prompt = prompt+ " Possible aggregates to use in the parameters include 'MIN', 'MAX', 'AVERAGE', 'SUM', and 'COUNT'.";
    
    prompt = prompt+ " Next generate a preamble for the answer. For example, 'The average temperature during December 2024 was '.";
    prompt = prompt+ " The actual numeric answer will be calculated by ThingWorx and added at the end of the preamble.";
    
    //Simple guardrails to keep user questions focused on the available data  
    prompt = prompt+ " If the user asks any kind of question that can't be directly answered using the data available in";
    prompt = prompt+ " the Data Table, or aggregated using the possible aggregates, then this is an invalid question.";
    prompt = prompt+ " Return a message that asks the user to try a different question, and offer suggestions that may";
    prompt = prompt+ " help the user ask a valid question.";
    
    prompt = prompt+ " If the user's question is valid, return the query between two '$' symbols, the aggregation";
    prompt = prompt+ " parameters between parentheses '(' and ')', and the preamble between the symbols '<' and '>'.";
    prompt = prompt+ " Don't add any underscores or other extraneous characters to the response.";
    prompt = prompt+ " Don't include any explanation in your response.";
    
    var result = prompt;
    
} catch(e) {
	logger.error("Error in GeneratePrompt: "+e+" at line "+e.lineNumber);   
    throw(e);
}

try {
    var questions = DataShapes["PTCDTS.TWXLLM.ExampleQuestion_DS"].CreateValues();
    questions.AddRow({question:"What was the maximum pressure observed on Machine3 during February 2025?"});
    questions.AddRow({question:"What was the average temperature recorded on Machine2 on December 12th, 2024?"});
    questions.AddRow({question:"What was the highest speed registered by Machine1 in January 2025?"});
    questions.AddRow({question:"How many hours was machine #1 running on February 22nd, 2025?"});
    questions.AddRow({question:"How many times did torque exceed 325 on machine 3 during the first week of January 2025?"});
    questions.AddRow({question:"What was the lowest rpm recorded for Machine2 between 3pm and 6pm on December 31, 2024?"});
    var result = questions;
} catch(e) {
	logger.error("Error in GetExampleQuestion: "+e+" at line "+e.lineNumber);   
    throw(e);
}

let result;
try {
    if (me.GPTAuth == '####' || me.GPTUrl == '####'){
        result = "Please make sure the correct GPTUrl and GPTAuth property values are set on the 'PTCDTS.TWXLLM.Manager' Thing.";
    } else {
        params = {
            headers: {
                "api-key": me.GPTAuth,
                "Content-Type": "application/json"
            },
            url: me.GPTUrl,
            content: {
                "messages": [{"role": "user", "content": prompt }],
                "top_p": top_p
            }
        };
        result = Resources["ContentLoaderFunctions"].PostJSON(params).choices[0].message.content.trim();
    }
} catch(e) {
	logger.error("Error in GetLLMTextResponse: "+e+" at line "+e.lineNumber);   
    throw(e);
}

try {
    var result;
    // Query data from the Data Table    
    let returnedData = Things["PTCDTS.TWXLLM.ExampleData_DT"].QueryDataTableEntries({
    	maxItems: 10000 /* NUMBER */, //Setting to 10000 ensures all relevant records for this data are returned 
    	query: query /* QUERY */,
    });
    
    // Use returnedData infotable with other parameters generated by LLM
    processedAggregateParams = {
    	t: returnedData /* INFOTABLE */,
    	columns: aggregateParams["columns"] /* STRING */,
    	aggregates: aggregateParams["aggregates"] /* STRING */,
    };
    
    let aggregatedResult = Resources["InfoTableFunctions"].Aggregate(processedAggregateParams);
    
    //Get name of the column of the aggregated result. This name will be constructed by TWX
    let aggregatedResultFields = aggregatedResult.dataShape.fields;
    let aggregatedResultFieldName = Object.keys(aggregatedResultFields)[0];
    
    // Round the result to 2 decimal places
    let roundedResult = parseFloat(aggregatedResult.rows[0][aggregatedResultFieldName]).toFixed(2);
    result = roundedResult;
} catch(e) {
	logger.error("Error in ProcessData: "+e+" at line "+e.lineNumber);   
    throw(e);
}

let result;

if (Things["PTCDTS.TWXLLM.ExampleData_DT"].GetDataStatus() != 'Data is ready.'){ // If data is not ready, request user to prepare data
    result = "Please prepare data before submitting questions.";
} else {
    let prompt = me.CreatePrompt({
    	userQuestion: userQuestion /* STRING */
    });
    //logger.warn("prompt "+prompt);
    
    let llmTextResponse = me.GetLLMTextResponse({
    	prompt: prompt /* STRING */,
    	top_p: undefined /* NUMBER {"defaultValue":0.1} */
    });
    //logger.warn("llmTextResponse "+llmTextResponse);
    
    // If the response from the LLM didn't include '[', then the question was not valid and the LLM created a retry message for the user.
    // If the response from the LLM did include '[', then the question was valid, and the Data Table query, aggregate
    // parameters, and the answer preamble are extracted for subsequent steps.
    let substring = '[';
    if (!llmTextResponse.includes(substring)){  
        result = llmTextResponse;
    } else {
        let query = llmTextResponse.split('$')[1];
    //    logger.warn("query "+query);
        
        let paramsIndex1 = llmTextResponse.indexOf('(');
    	let paramsIndex2 = llmTextResponse.indexOf(')');
        let aggregateParams = llmTextResponse.substring(paramsIndex1 + 1, paramsIndex2);
    //    logger.warn("aggregateParams "+aggregateParams);
        
        let preambleIndex1 = llmTextResponse.indexOf('<');
    	let preambleIndex2 = llmTextResponse.indexOf('>');
        let preamble = llmTextResponse.substring(preambleIndex1 + 1, preambleIndex2);
    //    logger.warn("preamble "+preamble);
        
        let aggregatedResult = me.ProcessData({
        	aggregateParams: aggregateParams /* JSON */,
        	query: query /* JSON */
        });
    //    logger.warn("aggregatedResult "+aggregatedResult);
        
        result = preamble + aggregatedResult + ".";
    }
}
