try {
    // jobInfoPage: INFOTABLE dataShape: "AnalyticsJobInfoPage"
    let jobInfoPage = Things[me.TWXAnalyticsServerName+"_TrainingThing"].GetJobInfos({limit: 1000});
    var jobInfoPageValues = jobInfoPage.rows[0].values;
    
    // flatten immportant values for clarity
    jobInfoPageValues.AddField({name: 'modelTJobId', baseType: 'STRING'}); // model training
    jobInfoPageValues.AddField({name: 'modelVTDJobId', baseType: 'STRING'}); // model validation on training data 
    jobInfoPageValues.AddField({name: 'modelVVDJobId', baseType: 'STRING'}); // model validation on validation data
    jobInfoPageValues.AddField({name: 'datasetUri', baseType: 'STRING'});
    jobInfoPageValues.AddField({name: 'goalName', baseType: 'STRING'});
    
    jobInfoPageValues.rows.toArray().forEach(row => {
        row.modelTJobId = row.jobTypesWithId.rows[0].fieldValue;
        if (row.jobTypesWithId.getRowCount() == 3){ // if model was validated on both training and validation data
            row.modelVTDJobId = row.jobTypesWithId.rows[1].fieldValue;
            row.modelVVDJobId = row.jobTypesWithId.rows[2].fieldValue;
        } else { // if model was validated only on validation data
            row.modelVTDJobId = 'undefined';
            row.modelVVDJobId = row.jobTypesWithId.rows[1].fieldValue;
        }
        row.datasetUri = row.datasetRef.rows[0].datasetUri;
        row.goalName = row.additionalParameters.rows[0].parameterValue;
    });
    //var result = jobInfoPageValues;
    
    
    //'aMJPrompt' stands for 'analyticsModelJobsPrompt'
    var aMJPrompt = 'Here is information about the Model jobs that were run for analytics purposes. ';
    aMJPrompt += 'There are several pieces of information associated with each job. ';
    aMJPrompt += 'Their descriptions are as follows. ';
