try {
    //'aMVRPrompt' stands for 'analyticsModelValidationResultsPrompt'
    var aMVRPrompt = '';
    aMVRPrompt += 'The results of the model validation are as follows. ';
    
    // There aren't really "results" to retrieve for a model, but we can access validation metrics
    // If a model trained successfully, and a validation holdout percentage was used (20% by default), there will be 
    // validation metrics on the validation data
    let validationResultsValidationData = Things[me.TWXAnalyticsServerName+"_ValidationThing"].RetrieveResult({jobId: modelVVDJobId});
    aMVRPrompt += 'Validation Data Record count: '+validationResultsValidationData.rows[0].recordCount+', ';
    aMVRPrompt += 'Validation Data RMSE: '+validationResultsValidationData.rows[0].validationMetrics.rows[0].rmse+', ';
    aMVRPrompt += 'Validation Data rSquared: '+validationResultsValidationData.rows[0].validationMetrics.rows[0].rSquared+', ';
    aMVRPrompt += 'Validation Data AUC: '+validationResultsValidationData.rows[0].validationMetrics.rows[0].areaUnderCurve+', ';
    
    // If a model trained successfully, and was marked to validate on the training data (80% by default), there
    // will be validation metrics on the training data
    if (modelVTDJobId != 'undefinded'){
        let validationResultsTrainingData = Things[me.TWXAnalyticsServerName+"_ValidationThing"].RetrieveResult({jobId: modelVTDJobId});
        aMVRPrompt += 'Training Data Record count: '+validationResultsTrainingData.rows[0].recordCount+', ';
        aMVRPrompt += 'Training Data RMSE: '+validationResultsTrainingData.rows[0].validationMetrics.rows[0].rmse+', ';
        aMVRPrompt += 'Training Data rSquared: '+validationResultsTrainingData.rows[0].validationMetrics.rows[0].rSquared+', ';
        aMVRPrompt += 'Training Data AUC: '+validationResultsTrainingData.rows[0].validationMetrics.rows[0].areaUnderCurve+', ';
    }
    
    var result = aMVRPrompt;
} catch(e) {
	logger.error("Error in GetAnalyticsModelValidationResults: "+e+" at line "+e.lineNumber);   
    throw(e);
}

