try {
    //'aPRPrompt' stands for 'analyticsProfilesResultsPrompt'
    var aPRPrompt = 'Here are the results of the Profiles job. ';
    
    let profilesResults = Things[me.TWXAnalyticsServerName+"_ProfilingThing"].RetrieveResult({jobId: profilesJobId});
    let profilesResultsValues = profilesResults.rows[0].conditions;
    //var result = profilesResultsValues;
    
    precision = 4; // round this many decimal places
    level = -1;
    profileCounter = 0;
    profilesResultsValues.rows.toArray().forEach(row => {
        aPRPrompt += 'Profile '+row.profileId+', ';
        aPRPrompt += 'fieldName: '+row.fieldName+', ';
        aPRPrompt += 'populationCount: '+row.populationCount+', ';
        aPRPrompt += 'populationPercentage: '+row.populationPercentage.toFixed(precision)+', ';
        aPRPrompt += 'averageGoal: '+row.averageGoal.toFixed(precision)+', ';
        aPRPrompt += 'value: '+row.value+', ';
        aPRPrompt += 'binMinimum: '+row.binMinimum+', ';
        aPRPrompt += 'binMaximum: '+row.binMaximum+', ';
        pause(1);
        if (row.profileId != profileCounter.toString()){
            profileCounter++;
            level = 0;
        } else {
            level++;
        }
        aPRPrompt += 'level: '+level.toString()+'. ';    
    });
    var result = aPRPrompt;
} catch(e) {
	logger.error("Error in GetAnalyticsProfilesResults: "+e+" at line "+e.lineNumber);   
