    let signalJobInfo = Things[me.TWXAnalyticsServerName+"_SignalsThing"].GetJobInfo({jobId: signalsJobId});
    let datasetUri = signalJobInfo.rows[0].datasetRef.rows[0].datasetUri;
    let goalField = signalJobInfo.rows[0].additionalParameters.rows[0].parameterValue;
    
    //'aSRPrompt' stands for 'analyticsSignalsResultsPrompt'
    let aSRPrompt = 'Once a Signals job completes, its result payload includes a ranked list of feature entries. For each feature ';
    aSRPrompt += 'there will be: ';
    aSRPrompt += 'signal Name: identical to the field name in the dataset. ';
    aSRPrompt += 'normalizedMutualInformation: a numeric score reflecting the featuregoal information share. ';
    aSRPrompt += 'informationGain: when redundancy filtering is on, the incremental gain beyond previously calculated features. ';
    aSRPrompt += 'redundantWithFeature: when redundancy filtering is on, the previously calculated feature it is redundant with. ';
    aSRPrompt += 'The first feature has no information gain value or redundant With Feature, subsequent Signals do. '; 
    aSRPrompt += 'Each signal will have a series of subgroups, and each subgroup will have these attributes: '; 
    aSRPrompt += '"binMin" and "binMax", which are numeric values that define range of value for that subgroup of the signal, '; 
    aSRPrompt += 'if that signal is numeric. If that signal is not numeric, there will not be values for "binMin" and "binMax", '; 
    aSRPrompt += 'and there instead be a "value" which lists the categorical value of that subgroup. '; 
    aSRPrompt += '"count" which is the number of records in the dataset that fall within that range. '; 
    aSRPrompt += '"averageGoal" which is the average value of the goal variable for the records that belong to that subgroup. '; 
    aSRPrompt += 'This granularity lets you answer questions like "Which grinder type more often leads to GrinderErrorOccurence?" ';
    aSRPrompt += 'or "Do higher or lower values of "AvgDailyCyclesPrior7Days" lead to grinder error occurrrence? ';
    aSRPrompt += 'The results of Signals job are as follows. ';
    
    let signalsResults = Things[me.TWXAnalyticsServerName+"_SignalsThing"].RetrieveResult({jobId: signalsJobId});
    let signalsResultsValues = signalsResults.rows[0].signals;
    
    let params = {infoTableName: 'datasetRef', dataShapeName: 'AnalyticsDatasetRef'};
    let datasetRef = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape(params);
    datasetRef.AddRow({datasetUri:datasetUri, format:'parquet'});
    
    let queryFields;
    signalCounter = 0;
    precision = 4; // round this many decimal places
    let tempFeatureName = '';
    signalsResultsValues.rows.toArray().forEach(row => {
        aSRPrompt += 'Signal '+signalCounter+' name: "'+row.features.rows[0].item+'", ';
        aSRPrompt += 'normalizedMutualInformation: '+row.normalizedMutualInformation.toFixed(precision)+', ';
        if (row.informationGain != undefined){ // If there is information gain, round it
            aSRPrompt += 'informationGain: ' + row.informationGain.toFixed(precision)+', ';
        } else {
            aSRPrompt += 'informationGain: ' + row.informationGain+', ';
        }
        aSRPrompt += 'redundantWithFeature: ' + row.redundantWithFeature+'. ';
        signalCounter++;
        
        let queryResults;
        try {// If signal is continuous, get distribution query with 10 bins
            queryResults = Things[me.TWXAnalyticsServerName+"_DataThing"].BinnedDistributionQuerySynchronous({
            	binnedField: row.features.rows[0].item,
                binCount: 10,
            	goalField: goalField,
            	datasetRef: datasetRef
            });    
            subgroupCounter = 0;
            queryResults.rows.toArray().forEach(queryResultsRow => {
                aSRPrompt += 'subgroup '+subgroupCounter+' binMin: '+queryResultsRow.min+', ';
                aSRPrompt += 'binMax: '+queryResultsRow.max+', ';
                aSRPrompt += 'count: '+queryResultsRow.count+', ';
                aSRPrompt += 'averageGoal: '+queryResultsRow.averageGoal.toFixed(precision)+'. ';
                subgroupCounter++;
            });
        } catch (e) { // If signal isn't continuous, get distribution of all categorical values       
            queryFields = DataShapes["GenericStringList"].CreateValues();
            queryFields.AddRow({item: row.features.rows[0].item});
            queryResults = Things[me.TWXAnalyticsServerName+"_DataThing"].DistributionQuerySynchronous({
            	queryFields: queryFields,
            	goalField: goalField,
            	datasetRef: datasetRef
            });
            subgroupCounter = 0;
            queryResults.rows.toArray().forEach(queryResultsRow => {
                aSRPrompt += 'subgroup '+subgroupCounter+' value: "'+queryResultsRow.groupByValues.rows[0].fieldValue+'", ';
                aSRPrompt += 'count: '+queryResultsRow.count+', ';
                aSRPrompt += 'averageGoal: '+queryResultsRow.averageGoal.toFixed(precision)+'. ';
                subgroupCounter++;
            });
        }
    });
    
    var result = aSRPrompt;
} catch(e) {
	logger.error("Error in GetAnalyticsSignalsResults: "+e+" at line "+e.lineNumber);   
    throw(e);
