try {
    var result;
    // Query data from the Data Table    
    let returnedData = Things["PTCDTS.TWXLLM.ExampleData_DT"].QueryDataTableEntries({
    	maxItems: 10000 /* NUMBER */, //Setting to 10000 ensures all relevant records for this data are returned 
    	query: query /* QUERY */,
    });
    
    // Use returnedData infotable with other parameters generated by LLM
    processedAggregateParams = {
    	t: returnedData /* INFOTABLE */,
    	columns: aggregateParams["columns"] /* STRING */,
    	aggregates: aggregateParams["aggregates"] /* STRING */,
    };
    
    let aggregatedResult = Resources["InfoTableFunctions"].Aggregate(processedAggregateParams);
    
    //Get name of the column of the aggregated result. This name will be constructed by TWX
    let aggregatedResultFields = aggregatedResult.dataShape.fields;
    let aggregatedResultFieldName = Object.keys(aggregatedResultFields)[0];
    
    // Round the result to 2 decimal places
    let roundedResult = parseFloat(aggregatedResult.rows[0][aggregatedResultFieldName]).toFixed(2);
    result = roundedResult;
} catch(e) {
	logger.error("Error in ProcessData: "+e+" at line "+e.lineNumber);   
    throw(e);
