    	datasetRef: datasetRef
    });
    me.DemoProfilesJobId = profilesJobId;
    
    // Wait until the Profiles job is done before moving on
    logger.info('working on profiles');
    checkJobIdCompletion(profilesJobId, 'Profiling');
    logger.info('done working on profiles');
    
    // Create ThingWorx Analytics predictive model 
    let modelTags = DataShapes["GenericStringList"].CreateValues();
    modelTags.AddRow({item:'buildermodel'});
    modelTags.AddRow({item:'builderfiltername:all_data'});
    
    // List what ML learner to use when training the model
    let learners = DataShapes["AnalyticsTrainingLearner"].CreateValues();
    learners.AddRow({learningTechnique:'NEURAL_NET', layerCount:3, hiddenUnitPercentage:0.25});
    
    let modelJobId = Things[me.TWXAnalyticsServerName+"_TrainingThing"].CreateJob({
    	jobName: 'Demo_Beanpro_Model',
    	maxAllowedFields: 25,
    	description: 'Demo Beanpro Espresso Predictive Model',
    	ensembleTechnique: 'SOLOIST',
    	tags: modelTags,
    	validationHoldoutPercentage: 0.2,
    	learners: learners,
    	goalField: 'GrinderErrorOccurrence',
    	datasetRef: datasetRef
    });
    
    me.DemoModelJobId = modelJobId;
    
} catch(e) {
	logger.error("Error in RunDemoAnalytics: "+e+" at line "+e.lineNumber);   
    throw(e);
}

function checkJobIdCompletion(jobId, jobType){
	while (Things[me.TWXAnalyticsServerName +'_'+ jobType + "Thing"].GetJobStatus({
			jobId: jobId
		}).rows[0].state != "COMPLETED") {
		if (Things[me.TWXAnalyticsServerName +'_'+ jobType + "Thing"].GetJobStatus({
				jobId: jobId
			}).rows[0].state == "FAILED") {
			throw "TWXA " + jobType + " Job " + jobId + " Failed: " + Things[me.TWXAnalyticsServerName +'_'+ jobType + "Thing"].GetJobStatus({
				jobId: jobId
			}).rows[0].message;
		} else {
			pause(1000); //1 second
		}
	}
}

// ThingWorx Service: TestGPT5MiniConnection
// Test service to debug GPT-5-mini API connection issues

var result;
var debugInfo = {};

try {
    // Input validation
    if (me.GPTAuth == '####' || me.GPTUrl == '####' || !me.GPTAuth || !me.GPTUrl) {
        result = "Please make sure the correct GPTUrl and GPTAuth property values are set on the 'PTCDTS.TWXLLM.Manager' Thing.";
//        return result;
    }
    
    // Clean URL to remove any hidden characters
    var cleanUrl = me.GPTUrl.trim();
    cleanUrl = cleanUrl.replace(/^\uFEFF/, '').replace(/^[\u200B-\u200D\uFEFF]/, '');
    
    // Log URL details for debugging
    logger.info("=== GPT-5-mini Connection Test ===");
    logger.info("URL: " + cleanUrl);
    logger.info("URL Length: " + cleanUrl.length);
    logger.info("Auth Key Length: " + me.GPTAuth.length);
    
    // Prepare test parameters
    var testPrompt = "Hello, please respond with 'API connection successful'";
    
    var params = {
        headers: {
            "api-key": me.GPTAuth,
            "Content-Type": "application/json"
        },
        url: cleanUrl,
        content: {
            "model": "gpt-5-mini",  // Explicitly specify model
            "messages": [
                {
                    "role": "user", 
                    "content": testPrompt
                }
            ],
            "max_tokens": 50,  // Limit tokens for testing
            "temperature": 0.1,  // Low temperature for consistent responses
            "top_p": 0.9
        }
    };
    
    logger.info("Request Parameters: " + JSON.stringify(params, null, 2));
    
    // Make API call
    logger.info("Making API call...");
    var apiResponse = Resources["ContentLoaderFunctions"].PostJSON(params);
    
    // Log full response
    logger.info("=== API Response ===");
    logger.info("Full Response: " + JSON.stringify(apiResponse, null, 2));
    
    // Store debug info
    debugInfo.url = cleanUrl;
    debugInfo.hasAuth = (me.GPTAuth && me.GPTAuth.length > 0);
    debugInfo.responseReceived = (apiResponse !== null && apiResponse !== undefined);
    
    // Parse response
    if (apiResponse) {
        debugInfo.responseType = typeof apiResponse;
        debugInfo.hasChoices = !!(apiResponse.choices);
        debugInfo.hasError = !!(apiResponse.error);
        
        if (apiResponse.error) {
            // Handle API error
            logger.error("API Error: " + JSON.stringify(apiResponse.error));
            result = "API Error: " + apiResponse.error.message || JSON.stringify(apiResponse.error);
            debugInfo.errorMessage = apiResponse.error.message;
            debugInfo.errorCode = apiResponse.error.code;
            
        } else if (apiResponse.choices && apiResponse.choices.length > 0) {
            // Success case
            result = apiResponse.choices[0].message.content.trim();
            debugInfo.success = true;
            debugInfo.responseLength = result.length;
            logger.info("Success! Response: " + result);
            
        } else {
            // Unexpected structure
            logger.warn("Unexpected response structure");
            result = "Unexpected API response structure. Check logs for details.";
            debugInfo.unexpectedStructure = true;
            debugInfo.responseKeys = Object.keys(apiResponse);
        }
    } else {
        // No response
        logger.error("No response received from API");
        result = "No response received from API";
        debugInfo.noResponse = true;
    }
    
} catch(e) {
    logger.error("=== Exception Details ===");
    logger.error("Error: " + e);
    logger.error("Error message: " + e.message);
    logger.error("Line number: " + e.lineNumber);
    logger.error("Stack trace: " + e.stack);
    
    debugInfo.exception = true;
    debugInfo.errorMessage = e.message;
    debugInfo.lineNumber = e.lineNumber;
    
    result = "Exception occurred: " + e.message;
}

// Log debug summary
logger.info("=== Debug Summary ===");
logger.info("Debug Info: " + JSON.stringify(debugInfo, null, 2));
logger.info("Final Result: " + result);

//return result;

// Clear out any existing or erroneous entries
me.PurgeDataTableEntries();

// CreateInfoTableFromDataShape(infoTableName:STRING("InfoTable"), dataShapeName:STRING):INFOTABLE(PTCDTS.TWXLLM.ExampleData_DS)
let exampleData = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
    infoTableName: "InfoTable",
    dataShapeName: "PTCDTS.TWXLLM.ExampleData_DS"
});

// Generating random data using the method Math.random() * (max - min) + min, and rounding to 2 decimal places
let startDatetime = new Date("11/01/2024 00:00:00");
startDatetime = dateAddHours(startDatetime, 4); // Adjusting for timezone
// var result = startDatetime;
const assets = ['Machine1', 'Machine2', 'Machine3'];
let row;
let randomNum;
let runningThreshold = 0.8; //gives 80% chance that an asset will be listed as "running"
for (let i = 0; i < 2880; i++) { // 2880 hours between 12 AM November 1, 2024 and 12 AM March 1, 2025.
    for (let j = 0; j < assets.length; j++) {
    	row = {};
        row.id = (i * assets.length + j).toString();
        row.DateTime = startDatetime;
        row.asset = assets[j];
        randomNum = Math.random();
        if (randomNum < runningThreshold){
        	row.status = 'running';
        } else {
            row.status = 'idle';
        }
        row.pressure = parseFloat(Math.random() * (150 - 50) + 50).toFixed(2);
        row.temperature = parseFloat(Math.random() * (120 - 20) + 20).toFixed(2);
        row.speed = parseFloat(Math.random() * (1000 - 100) + 100).toFixed(2);
        row.vibration = parseFloat(Math.random() * (5 - 0) + 0).toFixed(2);
        row.voltage = parseFloat(Math.random() * (240 - 210) + 210).toFixed(2);
        row.flow = parseFloat(Math.random() * (100 - 10) + 10).toFixed(2);
        row.torque = parseFloat(Math.random() * (500 - 100) + 100).toFixed(2);
        row.rpm = parseFloat(Math.random() * (1500 - 500) + 500).toFixed(2);
        row.power = parseFloat(Math.random() * (500 - 50) + 50).toFixed(2);
        exampleData.AddRow(row);
    }
	startDatetime = dateAddHours(startDatetime, 1);
}
//var result = exampleData;

me.AddDataTableEntries({
	values: exampleData /* INFOTABLE */
});

let result;
let currentRowCount = me.GetDataTableEntryCount();
// 2880 hours from Nov 1, 2024 - Feb 28, 2025, 3 assets. 2880 * 3 = 8640
let pctComplete = parseFloat((currentRowCount / 8640) * 100).toFixed(2); 
if (pctComplete == 100){ 
    result = "Data is ready.";
} else if (pctComplete == 0){
    result = "Click 'Prepare Data' to begin.";
} else {
    result = "Preparing data: "+pctComplete+"% complete.";
}
